local_save_progress();
function local_save_progress() {
	if (localStorage.getItem('level_1dsamdksa') == null) {
		localStorage.setItem('level_1dsamdksa', 'NO COMPLETE');
	}
	if (localStorage.getItem('level_1dsamdksa') != 'ORIGINAL: COMPLETE') {
	    localStorage.setItem('level_1dsamdksa', 'ORIGINAL: NO COMPLETE');
	}
	document.querySelector('.lvl1 > .progress_verife').innerHTML = localStorage.getItem('level_1dsamdksa');
}

function lvls_load() {
	document.querySelector('.official_lvl').style.display = 'block';
	document.querySelector('.creating').style.display = 'none';
	document.querySelector('.HP').style.display = 'none';
	let lvl1 = document.querySelector('.lvl1');
	let lvl2 = document.querySelector('.lvl2');
	let lvl3 = document.querySelector('.lvl3');
	
}

function lvls_load_back() {
	document.querySelector('.official_lvl').style.display = 'none';
	document.querySelector('.creating').style.display = 'block';
	document.querySelector('.HP').style.display = 'block';
}

function back_lvl() {
	if (lvl_this > 1) {
		lvl_this--;
		lvl_control.style.left = lvl_control.offsetLeft + 858 + 'px';
		document.querySelector('.back_lvl').style.display = 'none';
		document.querySelector('.to_lvl').style.display = 'none';
		setTimeout(function() {
			document.querySelector('.back_lvl').style.display = 'block';
			document.querySelector('.to_lvl').style.display = 'block';
		}, 400)
	}
}

function to_lvl() {
	if (lvl_this < 3) {
		lvl_this++;
		lvl_control.style.left = lvl_control.offsetLeft - 858 + 'px';
		document.querySelector('.back_lvl').style.display = 'none';
		document.querySelector('.to_lvl').style.display = 'none';
		setTimeout(function() {
			document.querySelector('.back_lvl').style.display = 'block';
			document.querySelector('.to_lvl').style.display = 'block';
		}, 400)
	}
}

function lvl1() {
	document.querySelector('.game > .blocks > *').outerHTML = lvl1Path_blocks;
	document.querySelector('.game > .blocks_lava > *').outerHTML = lvl1Path_blocks_lava;
	document.querySelector('.creating').style.display = 'none';
	let blocks_for_outer = document.querySelectorAll('.game > .blocks > *');
	for (let i = 0; i < blocks_for_outer.length; i++) {
		blocks_for_outer[i].style.top = blocks_for_outer[i].offsetTop - 110 + 'px';
		blocks_for_outer[i].style.left = blocks_for_outer[i].offsetLeft + 90 + 'px';
	}
	let blocks_lava_for_outer = document.querySelectorAll('.game > .blocks_lava > *');
	for (let i = 0; i < blocks_lava_for_outer.length; i++) {
		blocks_lava_for_outer[i].style.top = blocks_lava_for_outer[i].offsetTop - 110 + 'px';
		blocks_lava_for_outer[i].style.left = blocks_lava_for_outer[i].offsetLeft + 90 + 'px';
	}
	start = 'play';
	restart_to_off_lvl()
	document.querySelector('.official_lvl').style.display = 'none';
	document.querySelector('.HP').style.display = 'block';
}

